terraform_code.main_tf:
provider "aws" {
  # LocalStack configuration
  endpoints {
    apigateway      = "http://localhost:4566"
    apigatewayv2    = "http://localhost:4566"
    cloudwatch      = "http://localhost:4566"
    dynamodb        = "http://localhost:4566"
    ec2             = "http://localhost:4566"
    ecs             = "http://localhost:4566"
    ecr             = "http://localhost:4566"
    elasticache     = "http://localhost:4566"
    eventbridge     = "http://localhost:4566"
    iam             = "http://localhost:4566"
    kinesis         = "http://localhost:4566"
    kms             = "http://localhost:4566"
    lambda          = "http://localhost:4566"
    rds             = "http://localhost:4566"
    route53         = "http://localhost:4566"
    s3              = "http://localhost:4566"
    secretsmanager  = "http://localhost:4566"
    ses             = "http://localhost:4566"
    sns             = "http://localhost:4566"
    sqs             = "http://localhost:4566"
    ssm             = "http://localhost:4566"
    stepfunctions   = "http://localhost:4566"
  }

  skip_credentials_validation = true
  skip_metadata_api_check     = true
  skip_requesting_account_id  = true

  access_key = "test"
  secret_key = "test"
  region     = var.aws_region
}

variable bastion_amios {
  description = "The Linux distribution for the AMI to be used for the bastion host instances."
  type = string
  default = "Amazon-Linux2-HVM"
}

variable bastion_host_name {
  description = "The value used for the name tag of the bastion host."
  type = string
  default = "LinuxBastion"
}

variable bastion_banner {
  description = "Amazon S3 object URL for the text file with the content to display upon SSH login. The bastion host must have permission to download the file from the S3 bucket."
  type = string
}

variable bastion_tenancy {
  description = "Bastion VPC tenancy (dedicated or default)."
  type = string
  default = "default"
}

variable bastion_instance_type {
  description = "Amazon EC2 instance type for the bastion instances."
  type = string
  default = "t3.micro"
}

variable enable_banner {
  description = "Choose "true" to display a banner when connecting to the bastion using SSH."
  type = string
  default = "false"
}

variable enable_tcp_forwarding {
  description = "Choose "true" to enable TCP forwarding."
  type = string
  default = "false"
}

variable enable_x11_forwarding {
  description = "Choose "true" to enable X11 forwarding."
  type = string
  default = "false"
}

variable key_pair_name {
  description = "Name of an existing public/private key pair. If you do not have one in this AWS Region, please create it before continuing. If left empty, AWS Systems Manager Session Manager can still be used to connect to the instance."
  type = string
}

variable num_bastion_hosts {
  description = "The number of bastion hosts to create. The maximum number is four."
  type = string
  default = 1
}

variable ondemand_percentage {
  description = "Percentage of on-demand instances versus spot instances. With the default of 100, the ratio will be 100% on-demand instances and 0% spot instances."
  type = string
  default = 100
}

variable public_subnet1_id {
  description = "ID of the public subnet 1 that you want to provision the first bastion into (for example, subnet-a0246dcd). If RemoteAccessCIDR is set to 'disabled-onlyssmaccess', enter the ID of a private subnet instead."
  type = string
}

variable public_subnet2_id {
  description = "ID of the public subnet 2 that you want to provision the second bastion into (for example, subnet-e3246d8e). If RemoteAccessCIDR is set to 'disabled-onlyssmaccess', enter the ID of a private subnet instead."
  type = string
}

variable ec2_metadata_put_response_hop_limit {
  description = "The desired HTTP PUT response hop limit for instance metadata requests. The larger the number, the further instance metadata requests can travel."
  type = string
  default = 2
}

variable ec2_metadata_http_tokens {
  description = "If set to "optional" instances will be able to use their IAM instance profile. If set to "required" amd "EC2MetadataPutResponseHopLimit" is set to 1, instances will not be able to access the IAM role. If set to "required" amd "EC2MetadataPutResponseHopLimit" is set greater than 1 instances must send a signed token header with any instance metadata retrieval requests."
  type = string
  default = "required"
}

variable qss3_bucket_name {
  description = "Name of the S3 bucket for your copy of the Quick Start assets. Keep the default name unless you are customizing the template. Changing the name updates code references to point to a new Quick Start location. This name can include numbers, lowercase letters, and hyphens, but do not start or end with a hyphen (-). See https://aws-quickstart.github.io/option1.html."
  type = string
  default = "aws-quickstart"
}

variable qss3_bucket_region {
  description = "The AWS Region where the Quick Start S3 bucket (QSS3BucketName) is hosted. When using your own bucket, you must specify this value."
  type = string
  default = var.aws_region
}

variable qss3_key_prefix {
  description = "S3 key prefix that is used to simulate a directory for your copy of the Quick Start assets. Keep the default prefix unless you are customizing the template. Changing this prefix updates code references to point to a new Quick Start location. This prefix can include numbers, lowercase letters, uppercase letters, hyphens (-), and forward slashes (/). End with a forward slash. See https://docs.aws.amazon.com/AmazonS3/latest/dev/UsingMetadata.html and https://aws-quickstart.github.io/option1.html."
  type = string
  default = "quickstart-linux-bastion/"
}

variable remote_access_cidr {
  description = "Allowed CIDR block or prefix list for external SSH access to the bastions."
  type = string
  default = "disabled-onlyssmaccess"
}

variable vpcid {
  description = "ID of the VPC (for example, vpc-0343606e)."
  type = string
}

variable alternative_initialization_script {
  description = "HTTPS format Amazon S3 object URL for your custom initialization script to run during setup. The bastion host must have permission to download the file from the S3 bucket."
  type = string
}

variable os_image_override {
  description = "The Region-specific image to use for the instance."
  type = string
}

variable alternative_iam_role {
  description = "An existing IAM role name to attach to the bastion. If left blank, a new role will be created."
  type = string
}

variable environment_variables {
  description = "A comma-separated list of environment variables for use in bootstrapping. Variables must be in the format "key=value". "Value" cannot contain commas."
  type = string
}

variable root_volume_size {
  description = "The size in GB for the root EBS volume."
  type = string
  default = 10
}

variable permissions_boundary_arn {
  description = "Will be attached to all created IAM roles to satisfy security requirements."
  type = string
}

variable role_path {
  description = "Will be attached to all created IAM roles to satisfy security requirements."
  type = string
}

