  default = var.aws_region
}

variable qss3_key_prefix {
  description = "S3 key prefix for the Quick Start assets. Quick Start key prefix can include numbers, lowercase letters, uppercase letters, hyphens (-), and forward slash (/)."
  type = string
  default = "quickstart-amazon-aurora-mysql/"
}

variable db_storage_encrypted {
  description = "To disable database encryption, choose 'false'."
  type = string
  default = "true"
}

variable db_auto_minor_version_upgrade {
  description = "Select 'true' to set up auto minor version upgrade."
  type = string
  default = "false"
}

variable db_back_track {
  description = "The target backtrack window, in seconds. To disable backtracking, set this value to 0."
  type = string
  default = 0
}

variable db_backup_retention_period {
  description = "The number of days for which automatic database snapshots are retained."
  type = string
  default = 35
}

variable db_engine_version {
  description = "Select Database Engine Version.  Aurora Serverless v2 with Aurora MySQL is supported for versions 3.02.0 and higher. Aurora I/O-Optimized configuration is available in Aurora MySQL version 3.03.1 and higher. For supported engines and Region availability for Aurora Serverless v2 with Aurora MySQL, refer https://docs.aws.amazon.com/AmazonRDS/latest/AuroraUserGuide/Concepts.Aurora_Fea_Regions_DB-eng.Feature.ServerlessV2.html"
  type = string
  default = "Aurora-MySQL-3.04.0-MySQL8.0.28"
}

variable db_export_log_to_cloudwatch {
  description = "Specify the comma-delimited list of database logs (error, slowquery, audit, general) to export to CloudWatch Logs."
  type = string
}

variable db_instance_class {
  description = "The database instance type.  Please see supported instance types for the MySQL version selected https://docs.aws.amazon.com/AmazonRDS/latest/AuroraUserGuide/Concepts.DBInstanceClass.html#Concepts.DBInstanceClass.SupportAurora"
  type = string
  default = "db.r6g.2xlarge"
}

variable db_master_username {
  description = "(Required) The database master username. Must begin with a letter and contain only alphanumeric characters."
  type = string
}

variable db_master_user_password {
  description = "(Required) The database master user password. Min 8 chars. Must include 1 uppercase, 1 lowercase, 1 number, 1 (non / @ " ') symbol. Ignored if 'Manage DB master user password with AWS Secrets Manager' option is set to 'true'."
  type = string
}

variable db_multi_az {
  description = "Specifies if the database instance is a multiple Availability Zone deployment."
  type = string
  default = "true"
}

variable db_name {
  description = "Name of the initial Aurora MySQL database to create."
  type = string
  default = "sampleapp"
}

variable db_port {
  description = "The port the instance will listen for connections on."
  type = string
  default = 3306
}

variable enable_enhanced_monitoring {
  description = "Set to 'true' to enable Enahanced Monitoring."
  type = string
  default = "true"
}

variable enable_event_subscription {
  description = "Enables event subscription to Notification List."
  type = string
  default = "true"
}

variable enable_iamdb_auth {
  description = "Enables IAM Database Authentication."
  type = string
  default = "true"
}

variable enable_performance_insights {
  description = "Enables RDS Performance Insights."
  type = string
  default = "true"
}

variable manage_master_user_password {
  description = "Set to 'true' to manage the master user password with AWS Secrets Manager."
  type = string
  default = "true"
}

variable monitoring_interval {
  description = "The interval, in seconds, between points when Enhanced Monitoring metrics are collected for the database."
  type = string
  default = 10
}

variable notification_list {
  description = "The email notification used to configure an SNS topic for sending CloudWatch alarm and RDS event notifications."
  type = string
  default = "db-ops@domain.com"
}

variable performance_insights_retention_period {
  description = "The number of days for which automatic database snapshots are retained. Specify days based on (month * 31), where month is a number of months from 1-23."
  type = string
  default = 7
}

variable serverless_min_capacity {
  description = "Required if "db.serverless" is chosen as the database instance class. Specify minimum Aurora Serverless v2 Capacity Units (ACUs) in the range of 0.5 to 128 in increments of 0.5. 1 ACU provides 2 GiB of memory and corresponding compute and networking."
  type = string
  default = 8
}

variable serverless_max_capacity {
  description = "Required if "db.serverless" is chosen as the database instance class. Specify maximum Aurora Serverless v2 Capacity Units (ACUs) in the range of 1 to 128 in increments of 0.5.  1 ACU provides 2 GiB of memory and corresponding compute and networking."
  type = string
  default = 64
}

variable storage_type {
  description = "Designates the storage type to associate with the Aurora DB cluster. Choose aurora-iopt1 for Aurora I/O Optimized storage. For details refer https://docs.aws.amazon.com/AmazonRDS/latest/AuroraUserGuide/Aurora.Overview.StorageReliability.html#aurora-storage-type"
  type = string
  default = "aurora"
}

variable application {
  description = "[Optional] Name of the application for the associated AWS resource."
  type = string
}

variable application_version {
  description = "[Optional] Version of the application."
  type = string
}

variable compliance {
  description = "[Optional] Compliance level for the AWS resource."
  type = string
}

variable confidentiality {
  description = "[Optional] Confidentiality classification of the data that is associated with the AWS resource."
  type = string
}

variable environment_stage {
  description = "[Optional] Environment stage of the associated AWS resource."
  type = string
  default = "none"
}

variable project_cost_center {
  description = "[Optional] Designates the cost center associated with the project of the given AWS resource."
  type = string
}

