module "efa" {
  source = "../../"

  # Autoscaling group
  name = "${var.name_prefix}-efa"

  vpc_zone_identifier = module.vpc.private_subnets
  min_size            = 0
  max_size            = 1
  desired_capacity    = 1

  # aws ec2 describe-instance-types --region eu-west-1 --filters Name=network-info.efa-supported,Values=true --query "InstanceTypes[*].[InstanceType]" --output text | sort
  instance_type = "c5n.9xlarge"
  image_id      = data.aws_ami.amazon_linux.id
  user_data     = base64encode(local.efa_user_data)

  network_interfaces = [
    {
      description                 = "EFA interface example"
      delete_on_termination       = true
      device_index                = 0
      associate_public_ip_address = false
      interface_type              = "efa"
    }
  ]

  tags = local.tags
}

################################################################################
# Instance Requirements
################################################################################

module "instance_requirements" {
  source = "../../"

  # Autoscaling group
  name = "${var.name_prefix}-instance-requirements"

  vpc_zone_identifier   = module.vpc.private_subnets
  min_size              = 0
  max_size              = 5
  desired_capacity      = 1
  desired_capacity_type = "vcpu"

  update_default_version = true
  image_id               = data.aws_ami.amazon_linux.id

  use_mixed_instances_policy = true
  mixed_instances_policy = {
    launch_template = {
      override = [
        {
          instance_requirements = {
            cpu_manufacturers                                       = ["amd"]
            local_storage_types                                     = ["ssd"]
            max_spot_price_as_percentage_of_optimal_on_demand_price = 60
            memory_gib_per_vcpu = {
              min = 2
              max = 4
            }
            memory_mib = {
              min = 2048
            },
            vcpu_count = {
              min = 2
              max = 4
            }
          }
        }
      ]
    }
  }
  instance_requirements = {

    accelerator_manufacturers = []
    accelerator_names         = []
    accelerator_types         = []
    # If you specify allowed_instance_types, you can't specify excluded_instance_types
    # allowed_instance_types = ["m*"]

    baseline_ebs_bandwidth_mbps = {
      min = 400
      max = 1600
    }

    burstable_performance = "excluded"
    cpu_manufacturers     = ["amazon-web-services", "amd", "intel"]
    # If you specify excluded_instance_types, you can't specify allowed_instance_types
    excluded_instance_types = ["t*"]
    instance_generations    = ["current"]
    local_storage_types     = ["ssd", "hdd"]

    max_spot_price_as_percentage_of_optimal_on_demand_price = 45

    memory_gib_per_vcpu = {
      min = 4
      max = 16
    }

    memory_mib = {
      min = 24
      max = 128
    }

    network_interface_count = {
      min = 1
      max = 16
    }

    vcpu_count = {
      min = 2
      max = 96
    }
  }

  tags = local.tags
}

################################################################################
# Instance Requirements - Accelerators
################################################################################

module "instance_requirements_accelerators" {
  source = "../../"

  # Requires access to g or p instance types in your account http://aws.amazon.com/contact-us/ec2-request
  create = false

  # Autoscaling group
  name = "${var.name_prefix}-instance-requirements-accelerators"

  vpc_zone_identifier   = module.vpc.private_subnets
  min_size              = 0
  max_size              = 5
  desired_capacity      = 1
  desired_capacity_type = "units"

  update_default_version = true
  image_id               = data.aws_ami.amazon_linux.id

  use_mixed_instances_policy = true
  mixed_instances_policy = {
    launch_template = {
      override = [
        {
          instance_requirements = {
            memory_gib_per_vcpu = {
              min = 4
              max = 16
            }
            memory_mib = {
              min = 16
            },
            vcpu_count = {
              min = 4
              max = 64
            }
          }
        }
      ]
    }
  }
  instance_requirements = {
    accelerator_count = {
      min = 1
      max = 8
    }

    accelerator_manufacturers = ["amazon-web-services", "amd", "nvidia"]
    accelerator_names         = ["a100", "v100", "k80", "t4", "m60", "radeon-pro-v520"]

    accelerator_total_memory_mib = {
      min = 4096
      max = 16384
    }

    accelerator_types = ["gpu", "inference"]
    bare_metal        = "excluded"

    baseline_ebs_bandwidth_mbps = {
      min = 400
      max = 16384
    }

    burstable_performance   = "excluded"
    cpu_manufacturers       = ["amazon-web-services", "amd", "intel"]
    excluded_instance_types = ["t*"]
    instance_generations    = ["current"]
    local_storage_types     = ["ssd", "hdd"]

    memory_gib_per_vcpu = {
      min = 4
      max = 16
    }

    memory_mib = {
      min = 24
      max = 99999 # seems to be a provider bug
    }

    network_interface_count = {
      min = 1
      max = 4
    }

    vcpu_count = {
      min = 2
      max = 999 # seems to be a provider bug
    }
  }

  tags = local.tags
}

################################################################################
# Target Tracking Customized Metrics Policy
################################################################################

module "target_tracking_customized_metrics" {
  source = "../../"

  # Autoscaling group
  name = "${var.name_prefix}-customized-metrics"

  vpc_zone_identifier = module.vpc.private_subnets
  min_size            = 0
  max_size            = 1
  desired_capacity    = 1

  image_id      = data.aws_ami.amazon_linux.id
  instance_type = "t3.micro"

  scaling_policies = {
    metric_math = {
      policy_type               = "TargetTrackingScaling"
      estimated_instance_warmup = 120
      target_tracking_configuration = {
        customized_metric_specification = {
          # https://docs.aws.amazon.com/autoscaling/ec2/userguide/ec2-auto-scaling-target-tracking-metric-math.html
          metrics = [
            {
              label = "Get the queue size (the number of messages waiting to be processed)"
              id    = "m1"
              metric_stat = {
                metric = {
                  namespace   = "AWS/SQS"
                  metric_name = "${var.name_prefix}-ApproximateNumberOfMessagesVisible"
                  dimensions = [
                    {
                      name  = aws_sqs_queue.this.name
                      value = "my-queue"
                    }
                  ]
                }
                stat = "Sum"
              }
              return_data = false
            },
            {
              label = "Get the group size (the number of InService instances)"
              id    = "m2"
              metric_stat = {
                metric = {
                  namespace   = "AWS/AutoScaling"
                  metric_name = "${var.name_prefix}-GroupInServiceInstances"
                  dimensions = [
                    {
                      name  = "${var.name_prefix}-customized-metrics-${local.name}"
                      value = "my-asg"
                    }
                  ]
                }
                stat = "Average"
              }
              return_data = true
            },
            {
              label       = "Calculate the backlog per instance"
              id          = "e1"
              expression  = "m1 / m2"
              return_data = false
            }
          ]
        }
        target_value = 100
      }
    }
  }

  tags = local.tags
}

